    val listpenyakit : ArrayList<penyakit>
    get() {
        val list = arrayListOf<penyakit>()
        for (position in NamaPenyakit.indices){
            val Penyakit = penyakit()
            Penyakit.nama = NamaPenyakit[position]
            Penyakit.detail = DetailPenyakit[position]
            Penyakit.photo = GambarPenyakit[position]
        list.add(Penyakit)
        }
        return list
    }

